//             <MapWithADirectionsRenderer 
//                     directions={this.props.directions}
//                     googleMapURL={this.props.googleMapURL}
//                     // origin={this.props.origin} 
//                     // destination={this.props.destination} 
//                     // travelMode={this.props.travelMode} 
//                     // drivingOptions={this.props.drivingOptions}
//                     /> 
//         )
//     }
// }


class DirectionsMap extends Component {
  constructor(props) {
    super(props);
  
  this.convertDirections = this.convertDirections.bind(this);
  }

  componentDidMount(){
    console.log(this.props);
    this.convertDirections()
  }

  convertDirections(){
    const bounds = this.props.directions.routes[0].bounds;
    const northeastlat = bounds.northeast.lat;
    const northeastlng = bounds.northeast.lng;

    const southwestlat = bounds.southwest.lat;
    const southwestlng = bounds.southwest.lng;

    const newLatLngBounds = google.maps.LatLngBounds([{lat: northeastlat, lng: northeastlng}, 
     {lat: southwestlat, lng: southwestlng}]);

    console.log(newLatLngBounds);
  }

  render() {
    return (
          <div>
          <LoadScript
              id="script-loader"
              googleMapsApiKey={this.props.googleMapURL}
              language={"en"}
              region={"EN"}
              version={"weekly"}
              libraries={[]}
              onLoad={() => console.log("script loaded")}
              loadingElement={<div>Loading...</div>}
            >
          <GoogleMap
            id="basic-map-example"
            mapContainerStyle={{
              height: "400px",
              width: "100%"
            }}
            options={({
                      disableDefaultUI: true,
                      gestureHandling: "none",
                
                    })}
            zoom={8}
            center={{
              lat: 41.8507300,
              lng:-87.6512600
            }}>
            <TrafficLayer/>
            {
            <DirectionsRenderer
              options={{ // eslint-disable-line react-perf/jsx-no-new-object-as-prop
                directions: this.props.directions
              }}
            />}
            </GoogleMap>
         
        </LoadScript>
          </div>
    );
  }
}

export default DirectionsMap;
